// src/api/index.js
import axios from 'axios'

const instance = axios.create({
  baseURL: 'http://localhost:8081',
  withCredentials: true        // 携带 cookie（后端已开启 session）
})
// 添加请求拦截器：自动携带 userId
instance.interceptors.request.use(
  (config) => {
    // 从 localStorage 中获取 userId
    const userId = localStorage.getItem('userId') || '';
    
    // 根据请求方法，将 userId 添加到参数中
    if (userId) {
      if (config.method === 'get') {
        // GET 请求：添加到 query 参数（params）
        config.params = { ...config.params, userId: userId };
      } else {
        // POST/PUT/DELETE 等：添加到请求体（data）
        config.data = { ...config.data, userId: userId };
      }
    }
    
    return config;
  },
  (error) => {
    // 处理请求错误
    return Promise.reject(error);
  }
);

export default instance
